<?php

namespace deluxcms\media;

use Yii;
/**
 * menu module definition class
 */
class media extends \yii\base\Module
{
    /**
     * @inheritdoc
     */
    public $controllerNamespace = 'deluxcms\media\controllers';
    public $useController = false;

    /**
     * @inheritdoc
     */
    public function init()
    {
        parent::init();

        // custom initialization code goes here
    }

    public function beforeAction($action)
    {
        if (!$this->useController) return false;
        return parent::beforeAction($action); // TODO: Change the autogenerated stub
    }

    public $webRoot = '@frontend/web';
    public $webUrl;

    public $allowTypes = ['image/gif', 'image/jpeg', 'image/png'];
    public $uploadDir = 'uploads';
    public $thumbnailDir = 'thumbnails';
    public $noImage = '@vendor/deluxcms/media/source/images/no_picture.png';


    public function getWebRoot()
    {
        return rtrim(Yii::getAlias($this->webRoot), '/') . '/';
    }

    public function getWebUrl()
    {
        return rtrim($this->webUrl, '/') . '/';
    }

    public function getAllowTypes()
    {
        return $this->allowTypes;
    }

    public function getUploadDir()
    {
        return rtrim($this->uploadDir, '/');
    }

    public function getThumbnailDir()
    {
        return rtrim($this->thumbnailDir, '/') . '/';
    }

    public function getNoImage()
    {
        return $this->noImage;
    }
}
