<?php

namespace deluxcms\member\models;

use Yii;
use yii\base\Model;

class updateMember extends Model
{
    public $nickname;
    public $sex;
    public $address;

    public function init()
    {
        parent::init(); // TODO: Change the autogenerated stub
        $this->setAttributes(Yii::$app->user->getIdentity()->attributes);
    }

    public function rules()
    {
        return [
            ['nickname', 'required', 'message' => '不能为空'],
            ['nickname', 'string', 'max' => 50, 'message' => '昵称的长度不能大于50位'],
            ['address', 'string', 'max' => 255, 'message' => '地址的长度不能大于255位'],
            ['sex', 'in', 'range' => [0, 1], 'message' => '非法操作'],
        ];
    }

    public function update()
    {
        if (!$this->validate()) return false;
        $member = Yii::$app->user->getIdentity();
        $member->setAttributes([
            'nickname' => $this->nickname,
            'sex' => $this->sex,
            'address' => $this->address,
        ]);

        if (!$member->save()) {
            $this->addError('nickname', '修改个人信息失败，失败信息：' . print_r($member->getErrors(), true));
            return false;
        }

        return true;
    }

    public function attributeLabels()
    {
        return [
            'nickname' => '昵称',
            'sex' => '性别',
            'address' => '地址',
        ];
    }
}